import "core-js/modules/es.iterator.constructor.js";
import "core-js/modules/es.iterator.map.js";
import EmployeeAutocomplete from '@/core/components/inputs/EmployeeAutocomplete';
import { APIService } from '@/core/util/services/api.service';
export default {
  name: 'ReviewersAutoComplete',
  components: {
    'employee-autocomplete': EmployeeAutocomplete
  },
  props: {
    includeEmployees: {
      type: String,
      default: 'onlyCurrent'
    },
    excludeEmployee: {
      type: Object,
      required: false,
      default: null
    }
  },
  setup() {
    const http = new APIService(window.appGlobal.baseUrl, '/api/v2/performance/trackers/reviewers');
    return {
      http
    };
  },
  methods: {
    async loadEmployees(searchParam) {
      return new Promise(resolve => {
        if (searchParam.trim()) {
          this.http.getAll({
            nameOrId: searchParam.trim(),
            empNumber: this.excludeEmployee == null ? null : this.excludeEmployee.id //to be added back when a seperate API is created for reviewers
          }).then(({
            data
          }) => {
            resolve(data.data.map(employee => {
              return {
                id: employee.empNumber,
                label: `${employee.firstName} ${employee.middleName} ${employee.lastName}`,
                isPastEmployee: employee.terminationId ? true : false
              };
            }));
          });
        } else {
          resolve([]);
        }
      });
    }
  }
};
